mapper.insert:

INSERT INTO notice(userid
     		   , title
     		   , content
     		   , rdate)
          VALUES (#{userid}
               , #{title}
               , #{content}
               , now())

 	SELECT MAX(noticeNo) FROM notice

mapper.select:
SELECT MAX(noticeNo) noticeNo
	FROM notice
	WHERE userid = #{userid}

mapper.update:
UPDATE notice 
SET title = #{title},
	content = #{content}
WHERE noticeNo = #{noticeNo}

UPDATE notice
SET delyn = 'y'
WHERE noticeNo = #{noticeNo}
