mapper.select:
SELECT vacaNo, userid, rdate, reason, (CASE state WHEN 0 THEN '대기' WHEN 1 THEN '진행중' WHEN 2 THEN '승인' WHEN 3 THEN '반려' WHEN 7 THEN '사용 완료' WHEN 9 THEN '철회' END ) AS state , startTime, endTime, startDate, endDate, subtime_nolunchtime , (CASE DAYOFWEEK(startDate) WHEN '1' THEN '(일)' WHEN '2' THEN '(월)' WHEN '3' THEN '(화)' WHEN '4' THEN '(수)' WHEN '5' THEN '(목)' WHEN '6' THEN '(금)' WHEN '7' THEN '(토)' END) startDate_dayOfWeek , (CASE DAYOFWEEK(endDate) WHEN '1' THEN '(일)' WHEN '2' THEN '(월)' WHEN '3' THEN '(화)' WHEN '4' THEN '(수)' WHEN '5' THEN '(목)' WHEN '6' THEN '(금)' WHEN '7' THEN '(토)' END) endDate_dayOfWeek , duration , IF(duration>1 , ADDTIME(IF(startTime<'13:00:00' AND endTime>='13:00:00' , SUBTIME(subtime_nolunchtime,'01:00:00') , subtime_nolunchtime), SEC_TO_TIME(TIME_TO_SEC('08:00:00')*(duration-1))) , IF(startTime<='12:00:00' AND endTime>='13:00:00' , SUBTIME(subtime_nolunchtime,'01:00:00') , subtime_nolunchtime)) as useTime FROM (SELECT *, SUBTIME( IF(endTime BETWEEN '12:00:01' AND '12:59:59','13:00:00',endTime), IF(startTime BETWEEN '12:00:00' AND '12:59:59','12:00:00',startTime)) AS subtime_nolunchtime , DATEDIFF(endDate, startDate)+1 as duration FROM vacation WHERE userid = #{userid}
AND startDate >= #{startDate}
AND startDate <= #{endDate}
AND startDate BETWEEN #{startDate} AND #{endDate}
) vaca_temp ORDER BY startDate DESC, vacaNo DESC;

	SELECT COUNT(*) 
		FROM vacation
		WHERE state NOT IN (3,9)
			AND (startDate BETWEEN #{startDate} AND #{endDate}
      			OR endDate BETWEEN #{startDate} AND #{endDate})
      	AND userid = #{userid}

SELECT MAX(vacaNo) vacaNo 
	FROM vacation 
	WHERE userid = #{userid}

mapper.update:
UPDATE vacation
	SET state = 9
	WHERE vacaNo = #{vacaNo}

update.foreach:
UPDATE user SET 
	useVaca = useVaca + #{item.useVaca}
	, keepVaca = keepVaca - #{item.useVaca}
	WHERE userid = #{item.userid};

UPDATE vacation
	SET state = 7
	WHERE userid = #{item.userId};
