mapper.select:
SELECT * FROM work
	WHERE userid = #{userid} 
		AND DATE = #{now}

mapper.insert:

INSERT INTO work(userid
     		   , date
     		   , start) 
          VALUES (#{userid}
               , #{date}
               , #{time})

mapper.update:
UPDATE work 
	SET end = #{time}
   WHERE userid = #{userid}
   	AND date = #{date}
