mapper.select:
SELECT * FROM work
	WHERE userid = #{userid} 
		AND DATE = #{now}

		SELECT SEC_TO_TIME(SUM(TIME_TO_SEC(SUBTIME(end, start))))
			FROM work 
    		WHERE date BETWEEN #{startDay} and #{endDay}

mapper.update:
UPDATE work 
	SET end = #{time}
   WHERE userid = #{userid}
   	AND date = #{date}
